#include "consoleGUI.h"

int consoleGUI::valikko() {
	cout	<< "Anna syote:\n"
			<< "1: Lisaa tili\n"
			<< "2: Muokkaaa tilia\n"
			<< "3: Poista tili\n"
			<< "0: Lopeta ohjelma\n";
	cin >> this->menuChoice;
	return menuChoice;
}

void consoleGUI::otsikko() {
	/*system("cls");*/
	cout	<< endl
			<< "-------------------\n"
			<< "|    Superpankki  |\n|    V 0.0.5 A    |\n"
			<< "-------------------\n"
			<< endl;

}

int consoleGUI::deleteAccountMenu() {
	cout << "Anna poistettavan tilin id:\n";
	cin >> this->menuChoice;
	return menuChoice;
}
